package conditional_statements;

public class If_elseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//even odd check
				int a=21;
				if (a%2==0)
				{
					System.out.println("a is even number");
				}
				else
				{
					System.out.println("a is odd number");
				}
	}

}
